Here's the new function in TestDecimal.java:
    @Test    public void testCastFromInt() throws Exception {
        // Function checks for casting from Float, Double to Decimal data types        try (RemoteServiceSet serviceSet = RemoteServiceSet.getLocalServiceSet();             Drillbit bit = new Drillbit(CONFIG, serviceSet);             DrillClient client = new DrillClient(CONFIG, serviceSet.getCoordinator())) {
            // run query.            bit.run();            client.connect();            enableDecimalDataType(client);            List<QueryDataBatch> results = runQuery(client, "/decimal/cast_int_decimal.json", "/input_simple_decimal.json");
            RecordBatchLoader batchLoader = new RecordBatchLoader(bit.getContext().getAllocator());
            QueryDataBatch batch = results.get(0);            assertTrue(batchLoader.load(batch.getHeader().getDef(), batch.getData()));

            // the first pair of values in the input consists of integers.  ignore the rest.            String decimal9Output[] = {"99.0000"};            String decimal38Output[] = {"123456789.0000"};
            Iterator<VectorWrapper<?>> itr = batchLoader.iterator();
            // Check the output of decimal9            ValueVector.Accessor dec9Accessor = itr.next().getValueVector().getAccessor();            ValueVector.Accessor dec38Accessor = itr.next().getValueVector().getAccessor();

            for (int i = 0; i < decimal9Output.length; i++) {                assertEquals(dec9Accessor.getObject(i).toString(), decimal9Output[i]);                assertEquals(dec38Accessor.getObject(i).toString(), decimal38Output[i]);            }            assertEquals(6, dec9Accessor.getValueCount());            assertEquals(6, dec38Accessor.getValueCount());
            batchLoader.clear();            for (QueryDataBatch result : results) {              result.release();            }        }    }
    public List<QueryDataBatch> runQuery(DrillClient client, String cast, String input) throws Exception {        String s1 = Files.toString(FileUtils.getResourceAsFile(cast), Charsets.UTF_8);        String s2 = s1.replace("#{TEST_FILE}", input);        if (isWindows()) {            s2 = s2.replace("\n", "\r\n");        }System.out.println("Query: " + s2);  // DAO DEBUG        List<QueryDataBatch> results;        try {            results = client.runQuery(org.apache.drill.exec.proto.UserBitShared.QueryType.PHYSICAL, s2);        }        catch (Exception ex) {            ex.printStackTrace();            Throwable cause = ex.getCause();            if (cause != null) {                System.out.println("Caused by: " + cause.toString());                cause.printStackTrace();             }             throw ex;        }        return results;    }

