//3.销毁dubbo protocol
static void destroyProtocol() {
        ExtensionLoader<Protocol> loader = ExtensionLoader.getExtensionLoader(Protocol.class);
        for (String protocolName : loader.getLoadedExtensions()) {
            try {
                Protocol protocol = loader.getLoadedExtension(protocolName);
                if (protocol != null) {
                    protocol.destroy();
                }
            } catch (Exception t) {
                LOGGER.warn("调用dubbo Protocol.destroy 失败.", t);
            }
        }
    }

//4.-销毁dubbo protocol结束, 开始释放 NettyClientChannelFactory
/**
     * 销毁netty client的线程资源.
     * */
    static void destroyNetty() throws NoSuchFieldException {
        final Field field = NettyClient.class.getDeclaredField("channelFactory");

        AccessController.doPrivileged(new PrivilegedAction<Object>() {

            @Override
            public Object run() {
                field.setAccessible(true);
                ChannelFactory channelFactory;
                try {
                    channelFactory = (ChannelFactory) field.get(NettyClient.class);
                    channelFactory.shutdown();
                } catch (IllegalAccessException e) {
                    LOGGER.info("销毁netty client线程资源失败.", e);
                }
                return null;
            }
        });
    }
