 private static <T> boolean withExtensionAnnotation(Class<T> type) {
        boolean isExtension =  type.isAnnotationPresent(SPI.class);
        if(!isExtension) {
            for (Class<?> ifc : type.getInterfaces()) {
                isExtension = withExtensionAnnotation(ifc);
                if (isExtension) {
                    return isExtension;
                }
            }

            Class<?> superclass = type.getSuperclass();
            if (superclass == null || Object.class == superclass) {
                return false;
            }
            return  withExtensionAnnotation(superclass);
        }
        return isExtension;
    }
