public Result invoke(Invoker<?> invoker, Invocation invocation) throws RpcException {
        RpcContext.getContext()
                .setInvoker(invoker)
                .setInvocation(invocation)
                .setLocalAddress(NetUtils.getLocalHost(), 0)
                .setRemoteAddress(invoker.getUrl().getHost(),
                        invoker.getUrl().getPort());
        if (invocation instanceof RpcInvocation) {
            ((RpcInvocation) invocation).setInvoker(invoker);
        }
        try {
            // TODO should we clear server context?
            RpcContext.removeServerContext();
            return invoker.invoke(invocation);
        } finally {
            // TODO removeContext? but we need to save future for RpcContext.getFuture() API. If clear attachments here, attachments will not available when postProcessResult is invoked.
            //check attachments share context sign
            checkShareContextSign();
            RpcContext.getContext().clearAttachments();
        }
    }

     private void checkShareContextSign() {
        int retryCount = 3;
        long retry_internal = RpcContext.getContext().getAttachments().size() < 1000 ? 10 : 20;
        for (int i = 0; i < retryCount; i++) {
            boolean isShareContext = RpcContext.getContext().getAttachments().containsKey(SHARE_CONTEXT_SIGN);
            if (!isShareContext) {
                return;
            }
            try {
                Thread.sleep(retry_internal);
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        }
        throw new RpcException("Concurrent Exception, the biz Thread is faster than dubbo thread");
    }
