```java
@Override
    protected void before(Advice advice) throws ProcessControlException, InvocationTargetException,
            NoSuchMethodException,
            IllegalAccessException {
        Map<String, String> localParametersMap = (Map<String, String>) advice.getParameterArray()[1];
        if (localParametersMap != null && localParametersMap.size() > 0) {
            Map<String, String> copyOfLocalMap = new HashMap<>(localParametersMap);
            copyOfLocalMap.remove(GROUP_KEY);
            copyOfLocalMap.remove(DEFAULT_KEY_PREFIX + GROUP_KEY);
            advice.changeParameter(1, copyOfLocalMap);
