@SuppressWarnings({"JavaDoc", "DefaultAnnotationParam"})
@Configuration
@MapperScan(basePackages = "com.blue.finance.repository.mapper", sqlSessionTemplateRef = "sqlSessionTemplate")
@EnableTransactionManagement(proxyTargetClass = false, mode = AdviceMode.PROXY, order = Ordered.LOWEST_PRECEDENCE)
public class DataConfig {

