    private final DataDeploy dataDeploy;

    private final IdentityDeploy identityDeploy;

    public DataConfig(DataDeploy dataDeploy, IdentityDeploy identityDeploy) {
        this.dataDeploy = dataDeploy;
        this.identityDeploy = identityDeploy;
    }

    @Bean
    public DataSource dataSource() {
        try {
            DataConfAttr dataConfAttr = generateDataConfAttr(dataDeploy, identityDeploy);
            return ShardingDataSourceFactory.createDataSource(dataConfAttr.getDataSources(), dataConfAttr.getShardingRuleConfiguration(), dataConfAttr.getProps());
        } catch (Exception e) {
            LOGGER.error("初始化数据源失败,e = ", e);
            throw new RuntimeException("初始化数据源失败,e = {}", e);
        }
