    protected List<URL> getAddresses(String serviceProtocolKey, URL consumerURL) {
        if (serviceProtocolKey.contains("/")) {
            int groupDelimiterIndex = serviceProtocolKey.indexOf("/");
            String groupKey = serviceProtocolKey.substring(0, groupDelimiterIndex);
            String withoutGroupServiceKey = serviceProtocolKey.substring(groupDelimiterIndex);
            List<URL> urls = new ArrayList<>();
            if ("*".equals(groupKey)) {
                for (String s : serviceUrls.keySet()) {
                    if (s.endsWith(withoutGroupServiceKey)) {
                        urls.addAll((List<URL>) serviceUrls.get(s));
                    }
                }
            } else if (COMMA_SPLIT_PATTERN.split(groupKey).length > 1) {
                String[] groups = groupKey.split(",");
                for (String group : groups) {
                    if (serviceUrls.containsKey(group + withoutGroupServiceKey)) {
                        urls.addAll((List<URL>) serviceUrls.get(group + withoutGroupServiceKey));
                    }
                }
            }
            if (!CollectionUtils.isEmpty(urls)) {
                return urls;
            }
        }
        return (List<URL>) serviceUrls.get(serviceProtocolKey);
    }
