public class ConsumerApplication {

    public static void main(String[] args) {
        JSONB.parseObject(JSONB.toBytes(new CustomException(new RuntimeException("test")), JSONWriter.Feature.WriteClassName,
                        JSONWriter.Feature.FieldBased,
                        JSONWriter.Feature.ErrorOnNoneSerializable,
                        JSONWriter.Feature.ReferenceDetection,
                        JSONWriter.Feature.WriteNulls,
                        JSONWriter.Feature.NotWriteDefaultValue,
                        JSONWriter.Feature.NotWriteHashMapArrayListClassName,
                        JSONWriter.Feature.WriteNameAsSymbol), Object.class,
                new ContextAutoTypeBeforeHandler(true, CustomException.class.getName(), RuntimeException.class.getName()),
                JSONReader.Feature.UseDefaultConstructorAsPossible,
                JSONReader.Feature.ErrorOnNoneSerializable,
                JSONReader.Feature.UseNativeObject,
                JSONReader.Feature.FieldBased);
    }

    public static class CustomException extends RuntimeException {
        public CustomException(String message) {
            super(message);
        }

        public CustomException(String message, Throwable cause) {
            super(message, cause);
        }

        public CustomException(Throwable cause) {
            super(cause);
        }

        public CustomException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {
            super(message, cause, enableSuppression, writableStackTrace);
        }
    }
}
