import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.*;

public class Simulation {

    private static final ExecutorService EXECUTORS = Executors.newFixedThreadPool(3);

    public static long time = 0;


    public static void main(String[] args) {

        List<Future> list = new ArrayList<>();
        for (int i = 0; i < 3; i++) {
            Cal cal = new Cal();
            time = i * (1900 - i * 100)  + 1900;
            list.add(EXECUTORS.submit(cal));
        }
        long startTime = System.currentTimeMillis();
        for (int i = 0; i < 3; i++) {
            Future f = list.get(i);
            try {
                System.out.println(f.get(2, TimeUnit.SECONDS));
            } catch (Exception e) {
                throw new RuntimeException("访问超时");
            }
        }
        System.out.println("主线程处理时间 ： " + (System.currentTimeMillis() - startTime));
        EXECUTORS.shutdown();

    }

    static class Cal implements Callable {


        @Override
        public Object call() throws Exception {
            Thread.sleep(time);
            return new String("hello ");
        }
    }

}
