public class IgniteExecutor implements JobExecutor {

    private final IgniteCompute igniteCompute;


    public IgniteExecutor(IgniteCompute igniteCompute) {
        this.igniteCompute = igniteCompute;
    }


    @Override
    public void execute(Job job) {
        try {
            IgniteRunnable runnable = job::run;
            if (job.isBroadcast()) {
                igniteCompute.broadcast(runnable);
            } else {
                igniteCompute.run(runnable);
            }
        } catch (Exception e) {
            ContextHolder.getReceiveContext()
                    .getLogManager()
                    .printError(null, LogEvent.CLUSTER, job.getJobName()+" execute  error "+e.getMessage());
        }

    }


    @Override
    public <R> Collection<R> callBroadcast(JobCaller<R> callable) {
        try {
            IgniteCallable<R> igniteCallable = callable::call;

            return igniteCompute.broadcast(igniteCallable);
        } catch (Exception e) {
            ContextHolder.getReceiveContext()
                    .getLogManager()
                    .printError(null, LogEvent.CLUSTER, callable.getJobName()+" callBroadcast  error "+e.getMessage());
            return Collections.emptySet();
        }

    }

    @Override
    public <INPUT, OUT> Collection<OUT> callBroadcast(JobClosure<INPUT, OUT> callable, INPUT input) {
        try {
            return this.igniteCompute.broadcast(callable, input);
        } catch (Exception e) {
            ContextHolder.getReceiveContext()
                    .getLogManager()
                    .printError(null, LogEvent.CLUSTER, callable.getJobName()+" callBroadcast error "+e.getMessage());
            return Collections.emptySet();
        }
    }


    @Override
    public <R> R call(JobCaller<R> callable) {
        try {
            IgniteCallable<R> igniteCallable = callable::call;
            return igniteCompute.call(igniteCallable);
        } catch (Exception e) {
            ContextHolder.getReceiveContext()
                    .getLogManager()
                    .printError(null, LogEvent.CLUSTER, callable.getJobName()+" call error "+e.getMessage());
            return null;
        }
    }

}

   public Collection<Map<String,String>> getClusterInfo() {
        return ContextHolder.getReceiveContext()
                    .getIntegrate().getJobExecutor()
                    .callBroadcast(new JobCaller<Map<String,String>>() {
                        @Override
                        public Map<String,String> call() throws Exception {
                            BootstrapConfig bootstrapConfig = ContextHolder.getReceiveContext().getConfig();
                            Map<String,String> map = new HashMap<>();
                            map.put("ip",Optional.ofNullable(bootstrapConfig.getClusterConfig().getLocalAddress()).orElse(ServerUtils.serverIp ));
                            String httpUrl =bootstrapConfig.getHttpConfig().getHost() + ":" + bootstrapConfig.getHttpConfig().getPort();
                            if (bootstrapConfig.getHttpConfig().getSsl() != null) {
                                map.put("httpAddress","https://" +httpUrl);
                            } else {
                                map.put("httpAddress","http://" +httpUrl);
                            }
                            return map;
                        }

                        @Override
                        public String getJobName() {
                            return "getClusterInfo";
                        }
                    });
    }
