    public class CdiParametersResolverExtension implements ParameterResolver, AfterEachCallback
    {
        private Map<Parameter, Instance> instances;
    
        @Override
        public boolean supportsParameter(final ParameterContext parameterContext, final ExtensionContext extensionContext)
                throws ParameterResolutionException {
            try
            {
                final Parameter parameter = parameterContext.getParameter();
                final BeanManager bm = CDI.current().getBeanManager();
                final Bean<?> bean = bm.resolve(bm.getBeans(parameter.getType(), Stream.of(parameter.getAnnotations())
                        .filter(it -> bm.isQualifier(it.annotationType()))
                        .toArray(Annotation[]::new)));
                if (bean == null)
                {
                    return false;
                }
                final CreationalContext<Object> creationalContext = bm.createCreationalContext(null);
                final Object instance = bm.getReference(bean, parameter.getType(), creationalContext);
                if (instances == null)
                {
                    instances = new HashMap<>();
                }
                instances.put(parameter, new Instance(instance, creationalContext));
                return true;
            }
            catch (final IllegalStateException ise) // no cdi container
            {
                return false;
            }
        }
    
        @Override
        public void afterEach(final ExtensionContext context)
        {
            if (instances != null) {
                instances.values().stream().map(i -> i.creationalContext).forEach(CreationalContext::release);
                instances.clear();
                instances = null;
            }
        }
    
        @Override
        public Object resolveParameter(final ParameterContext parameterContext, final ExtensionContext extensionContext)
                throws ParameterResolutionException
        {
            return instances.get(parameterContext.getParameter()).instance;
        }
    
        private static class Instance
        {
            private final Object instance;
            private final CreationalContext<?> creationalContext;
    
            private Instance(final Object instance, final CreationalContext<?> creationalContext)
            {
                this.instance = instance;
                this.creationalContext = creationalContext;
            }
        }
    }


