Added key/value labels and encoded the JSON. Will have to test to see if we can go further with this example.

```
package main

import "encoding/json"
import "fmt"

type KeyValue struct {
    Key  string     `json:"key"`
    Value interface{}   `json:"value"`
}

func main() {
    bytes := []byte(`{"a":"A","b":["B","C"], "testBool": false}`)
    var data map[string]interface{}
    if err := json.Unmarshal(bytes, &data); err != nil {
        panic(err)
    }

    keys := make([]string, len(data))
    values := make([]interface{}, len(data))

    i := 0
    for k, v := range data {
       keys[i] = k
       values[i] = v

    keyValue := KeyValue{
        Key:  keys[i],
        Value: values[i],

        }

       p, _ := json.Marshal(keyValue)
       s := string(p)
       fmt.Println("str " + s)

       i++
    }
}
