private static void appendEscaped(StringBuilder builder, char character) {

        switch (character) {
            case '*':
                builder.append("\\" + character);
                break;
            case '+':
                builder.append("\\" + character);
                break;
            case '-':
                builder.append("\\" + character);
                break;
            case '#':
                builder.append("\\" + character);
                break;
            case 34:
                builder.append("\"");
                break;
            case 38:
                builder.append("&");
                break;
            case 39:
                builder.append("\'");
                break;
            case 60:
                builder.append("<");
                break;
            case 62:
                builder.append(">");
                break;
            case 178:
                builder.append("<sup>2</sup>");
                break;
            case 179:
                builder.append("<sup>3</sup>");
                break;

            default:
                builder.append(character);
                break;
        }
    }

```
