import org.apache.rocketmq.spring.annotation.RocketMQMessageListener;
import org.apache.rocketmq.spring.core.RocketMQListener;
import org.apache.rocketmq.spring.core.RocketMQReplyListener;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

@Component
@RocketMQMessageListener(consumerGroup = "${rocketmq.consumer.group}", topic = "${consumer.topic}")
public class RocketMQConsumer001 implements RocketMQListener<String> {
    @Value("${consumer.topic}")
    private String topicName;
    @Value("${rocketmq.consumer.group}")
    private String groupName;

    @Override
    public void onMessage(String s) {
        System.out.println("groupName :" + groupName + "：from topicName: " + topicName+",监听的消息内容为："+s);
    }
}
