//                    TimeUnit.SECONDS.sleep(60);
//                    return LocalTransactionState.COMMIT_MESSAGE;
//                } catch (InterruptedException e) {
//                    Thread.currentThread().interrupt();
//                }
//                return LocalTransactionState.UNKNOW;
                return LocalTransactionState.UNKNOW;
            }

            @Override
            public LocalTransactionState checkLocalTransaction(MessageExt msg) {
                log.info("Broker check my transaction state");
                return LocalTransactionState.COMMIT_MESSAGE;
            }
        });
        producer.start();
    }

    public TransactionSendResult sendOrderTransactionMsg(String content) {
        Message msg = new Message("order_transaction_topic", content.getBytes());
        try {
            TransactionSendResult result = producer.sendMessageInTransaction(msg, "myArg");
            log.info("sending result:{}", result);
        } catch (MQClientException e) {
            log.error("exception", e);
        }
        return null;
    }
}

