public class EmployeeSaltedAuthenticationInfo implements SaltedAuthenticationInfo {

    private final Object username;
    private final String password;
    private final String salt;
    private final String name;

    public EmployeeSaltedAuthenticationInfo(Object username, String password, String salt, String name) {
        this.username = username;
        this.password = password;
        this.salt = salt;
        this.name = name;
    }

    @Override
    public ByteSource getCredentialsSalt() {
        return ByteSource.Util.bytes(salt);
    }

    @Override
    public PrincipalCollection getPrincipals() {
        return new SimplePrincipalCollection(username, name);
    }

    @Override
    public Object getCredentials() {
        return password;
    }
}

