@Warmup(iterations = 5, time = 5, timeUnit = TimeUnit.SECONDS)
@Measurement(iterations = 5, time = 5, timeUnit = TimeUnit.SECONDS)
@State(Scope.Benchmark)
public class FilterQueryTest {

    private RestHighLevelClient client;

    private final long startTimestamp = 1580287478790L;
    private final long endTimestamp = 1680480640000L;

    private final long MULTIPLE = 10000L;
    private final int SEARCH_SIZE = 5000;
    private final String INDEX_NAME = "service_inventory";

    @Setup
    public void createClient() {
        client = new RestHighLevelClient((RestClient.builder(new HttpHost("127.0.0.1", 9200, "http"))));
    }

    @TearDown
    public void closeClient() throws IOException {
        client.close();
    }

    /**
     * Benchmark test fix es query clause
     */
    @Benchmark
    @BenchmarkMode(Mode.AverageTime)
    @OutputTimeUnit(TimeUnit.MICROSECONDS)
    public void testFixEsQuery() throws IOException {
        SearchSourceBuilder sourceBuilder = SearchSourceBuilder.searchSource();

        BoolQueryBuilder boolQueryBuilder = QueryBuilders.boolQuery();
        boolQueryBuilder.should().add(timeRangeQueryBuild(startTimestamp, endTimestamp));

        boolQueryBuilder.should().add(QueryBuilders.termQuery("is_address", 0));
        boolQueryBuilder.should().add(QueryBuilders.termQuery("node_type", 0));

        sourceBuilder.query(boolQueryBuilder);
        sourceBuilder.size(SEARCH_SIZE);
        SearchRequest searchRequest = new SearchRequest(INDEX_NAME);
        searchRequest.source(sourceBuilder);
        SearchResponse response = client.search(searchRequest, RequestOptions.DEFAULT);
    }

