private boolean hasNoUserSuppliedProxyInterfaces(AdvisedSupport config) {                                  
	Class[] interfaces = config.getProxiedInterfaces();                                                    
	return (interfaces.length == 0 || (interfaces.length == 1 && SpringProxy.class.equals(interfaces[0])));
}                                                                                                          
