    @Trace
    @KafkaListener(topics = "${spring.application.name}", groupId = "${spring.application.name}")
    private void receiveMessage(ConsumerRecord<?, ?> record) {
        Optional<?> kafkaMessage = Optional.ofNullable(record.value());
        if (kafkaMessage.isPresent()) {
            Object message = kafkaMessage.get();
            QueueMessageDTO queueMessageDTO = JSONUtil.toBean(String.valueOf(message), QueueMessageDTO.class);
            log.info("swTraceId:{},swTraceId:{},uuid: {} ,message: {},targetId:{}", TraceContext.traceId(), TraceContext.getCorrelation("swTraceId"),
                    TraceContext.getCorrelation("uuid"), queueMessageDTO.getCommand(), queueMessageDTO.getTargetServiceId());
        }
    }
