package org.apache.skywalking.apm.agent.core.pool.druid;


import org.apache.skywalking.apm.agent.core.logging.api.ILog;
import org.apache.skywalking.apm.agent.core.logging.api.LogManager;
import org.apache.skywalking.apm.agent.core.meter.MeterFactory;
import org.apache.skywalking.apm.agent.core.pool.DatabaseParserHelper;

import javax.management.MBeanServer;
import javax.management.ObjectName;
import java.lang.management.ManagementFactory;
import java.util.Hashtable;
import java.util.LinkedHashMap;
import java.util.Map;
import java.util.Set;

public class DruidRegister {
    private static final ILog LOGGER = LogManager.getLogger(DruidRegister.class);

    protected MBeanServer mbeanServer = ManagementFactory.getPlatformMBeanServer();
    protected DatabaseParserHelper databaseParser = new DatabaseParserHelper();
    private static final String ERROR_ATTRIBUTE = "unknown";


    public boolean register() {
        try {
            LOGGER.info("DruidRegister register start");
            Hashtable<String, String> table = new Hashtable<String, String>();
            table.put("type", "DruidDataSource");
            table.put("id", "*");
            ObjectName pooledDataSourceObjectName = new ObjectName("com.alibaba.druid", table);
            Set<ObjectName> objectNameSet = mbeanServer.queryNames(pooledDataSourceObjectName, null);

            if (objectNameSet == null || objectNameSet.isEmpty()) {
                return false;
            }

            Map<String, Integer> datasources = new LinkedHashMap<String, Integer>();

            int index = 0;
            for (ObjectName objectName : objectNameSet) {
                String jdbcUrl = getStringAttribute(objectName, "Url");
                DatabaseParserHelper.Database datasource = databaseParser.parseDatabase(jdbcUrl);
                String key = getConnection(datasources, datasource.toString());
                LOGGER.debug("key {}", key);

                MeterFactory.gauge("active_count", () -> getIntegerAttribute(objectName, "ActiveCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("pooling_count", () -> getIntegerAttribute(objectName, "PoolingCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("idle_connection", () -> getIntegerAttribute(objectName, "ActiveCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("lock_queue_length", () -> getIntegerAttribute(objectName, "LockQueueLength").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("max_wait_thread_count", () -> getIntegerAttribute(objectName, "MaxWaitThreadCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("commit_count", () -> getLongAttribute(objectName, "CommitCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("connect_count", () -> getLongAttribute(objectName, "ConnectCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("connect_error_count", () -> getLongAttribute(objectName, "ConnectErrorCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                MeterFactory.gauge("create_error_count", () -> getLongAttribute(objectName, "CreateErrorCount").doubleValue()).tag("index", String.valueOf(index)).tag("datasource", key).build();
                index++;
            }
            LOGGER.info("DruidRegister register end");
        } catch (Exception e) {
            LOGGER.error("Druid Register fail", e);
        }
        return true;
    }

    protected String getConnection(Map<String, Integer> datasources, String key) {
        Integer index = datasources.get(key);

        if (index == null) {
            datasources.put(key, 0);

            return key;
        } else {
            index++;

            datasources.put(key, index);
            return key + '[' + index + ']';
        }
    }

    protected Integer getIntegerAttribute(ObjectName objectName, String attribute) {
        try {
            Integer value = (Integer) mbeanServer.getAttribute(objectName, attribute);
            return value;
        } catch (Exception e) {
            return 0;
        }
    }

    protected Long getLongAttribute(ObjectName objectName, String attribute) {
        try {
            Long value = (Long) mbeanServer.getAttribute(objectName, attribute);
            return value;
        } catch (Exception e) {
            return 0L;
        }
    }

    protected String getStringAttribute(ObjectName objectName, String attribute) {
        try {
            return (String) mbeanServer.getAttribute(objectName, attribute);
        } catch (Exception e) {
            return ERROR_ATTRIBUTE;
        }
    }

}
