package org.apache.skywalking.apm.agent.core.pool;

import org.apache.skywalking.apm.agent.core.boot.BootService;
import org.apache.skywalking.apm.agent.core.boot.DefaultImplementor;
import org.apache.skywalking.apm.agent.core.boot.DefaultNamedThreadFactory;
import org.apache.skywalking.apm.agent.core.logging.api.ILog;
import org.apache.skywalking.apm.agent.core.logging.api.LogManager;
import org.apache.skywalking.apm.agent.core.pool.druid.DruidRegister;
import org.apache.skywalking.apm.util.RunnableWithExceptionProtection;

import java.util.concurrent.Executors;
import java.util.concurrent.ScheduledFuture;
import java.util.concurrent.TimeUnit;

/**
 * The <code>PoolService</code> represents a timer, which register Druid monitor metrics
 * {@link DruidRegister}
 */
@DefaultImplementor
public class PoolService implements BootService, Runnable {
    private static final ILog LOGGER = LogManager.getLogger(PoolService.class);
    private volatile ScheduledFuture<?> registerMetricFuture;
    private DruidRegister druidRegister;

    @Override
    public void prepare() throws Throwable {
        druidRegister = new DruidRegister();
    }

    @Override
    public void boot() throws Throwable {
        registerMetricFuture = Executors.newSingleThreadScheduledExecutor(
                new DefaultNamedThreadFactory("PoolService-Register"))
                .scheduleAtFixedRate(new RunnableWithExceptionProtection(
                        this,
                        new RunnableWithExceptionProtection.CallbackWhenException() {
                            @Override
                            public void handle(Throwable t) {
                                LOGGER.error("PoolService Register metrics failure.", t);
                            }
                        }
                ), 0, 30, TimeUnit.SECONDS);
    }

    @Override
    public void onComplete() throws Throwable {

    }

    @Override
    public void shutdown() throws Throwable {
        registerMetricFuture.cancel(true);
    }

    @Override
    public void run() {
        druidRegister.register();
    }
}
