/**
 *
 * 基于spring boot环境
 */
public class DemoLogbackSystem extends LogbackLoggingSystem {
	
	private ClassLoader classLoader;
	
	public DemoLogbackSystem(ClassLoader classLoader) {
		super(classLoader);
		this.classLoader = classLoader;
	}
	
	@Override
	protected void loadDefaults(LoggingInitializationContext initializationContext, LogFile logFile) {
		LoggerContext context = getLoggerContext();
		context.stop();
		context.reset();
		
		customFileLog(context);
	}
	
	@Override
	protected void loadConfiguration(LoggingInitializationContext initializationContext, String location, LogFile logFile) {
		LoggerContext loggerContext = getLoggerContext();
		
		super.loadConfiguration(initializationContext, location, logFile);
		
		customFileLog(loggerContext);
	}
	
	private LoggerContext getLoggerContext() {
		ILoggerFactory factory = StaticLoggerBinder.getSingleton().getLoggerFactory();
		return (LoggerContext) factory;
	}
	
	private void customFileLog(LoggerContext context) {
		List<DemoLogInitializer> logInitializers = SpringFactoriesLoader.loadFactories(DemoLogInitializer.class, getClassLoader());
		for (DemoLogInitializer logInitializer : logInitializers) {
			logInitializer.init(context);
		}
	}
	
	public static interface DemoLogInitializer {
		void init(LoggerContext context);
	}
	
	public static class DemoFileInitializer implements DemoLogInitializer {
		
		private LoggerContext context;
		
		@Override
		public void init(LoggerContext context) {
			this.context = context;
			
			String fileName = "app-info-30de.log";
			Appender<ILoggingEvent> appender = asyncFileAppender("FILE-INFO", getPattern(), fileName);
			
			root(Level.INFO, appender);
		}
		
		private Appender<ILoggingEvent> asyncFileAppender(String appenderName, String pattern, String fileName) {
			
			LogbackAsyncAppender logbackAsyncAppender = new LogbackAsyncAppender();
			logbackAsyncAppender.setContext(context);
			logbackAsyncAppender.setName("ASYNC-" + appenderName);
			
			String logFile = "/var/logpath/" + fileName;
			RollingFileAppender<ILoggingEvent> appender = new RollingFileAppender<>();
			
			DemoTraceIdPatternLayoutEncoder encoder = new DemoTraceIdPatternLayoutEncoder();
			encoder.setPattern(pattern);
			appender.setEncoder(encoder);
			start(encoder);
			
			appender.setFile(logFile);
			
			TimeBasedRollingPolicy rollingPolicy = new TimeBasedRollingPolicy();
			rollingPolicy.setFileNamePattern(logFile + ".%d{yyyy-MM-dd}.%i");
			rollingPolicy.setParent(appender);
			rollingPolicy.setMaxHistory(0);
			rollingPolicy.setCleanHistoryOnStart(true);
			
			SizeAndTimeBasedFNATP<ILoggingEvent> triggeringPolicy = new SizeAndTimeBasedFNATP<>();
			triggeringPolicy.setMaxFileSize(new FileSize(GB_COEFFICIENT));
			triggeringPolicy.setTimeBasedRollingPolicy(rollingPolicy);
			rollingPolicy.setTimeBasedFileNamingAndTriggeringPolicy(triggeringPolicy);
			
			start(rollingPolicy);
			
			appender.setTriggeringPolicy(triggeringPolicy);
			appender.setRollingPolicy(rollingPolicy);
			
			appender.setName(appenderName);
			start(appender);
			
			logbackAsyncAppender.addAppender(appender);
			
			start(logbackAsyncAppender);
			
			return logbackAsyncAppender;
		}
		
		private void root(Level level, Appender<ILoggingEvent>... appenders) {
			Logger logger = this.context.getLogger(org.slf4j.Logger.ROOT_LOGGER_NAME);
			if (level != null) {
				logger.setLevel(level);
			}
			for (Appender<ILoggingEvent> appender : appenders) {
				logger.addAppender(appender);
			}
		}
		
		private String getPattern() {
			return OptionHelper.substVars(pattern(), this.context);
		}
		
		private void start(LifeCycle lifeCycle) {
			if (lifeCycle instanceof ContextAware) {
				((ContextAware) lifeCycle).setContext(this.context);
			}
			lifeCycle.start();
		}
	}
	
	public static class DemoTraceIdPatternLayoutEncoder extends PatternLayoutEncoderBase<ILoggingEvent> {
		@Override
		public void start() {
			PatternLayout patternLayout = new TraceIdPatternLogbackLayout();
			patternLayout.setContext(context);
			patternLayout.setPattern(getPattern());
			patternLayout.setOutputPatternAsHeader(outputPatternAsHeader);
			patternLayout.start();
			this.layout = patternLayout;
			super.start();
		}
	}
	
}
