@BenchmarkMode(Mode.Throughput)
@OutputTimeUnit(TimeUnit.SECONDS)
@State(Scope.Thread)
@Fork(2)
@Warmup(iterations = 4)
@Measurement(iterations = 5)
public class SynchronizedTest {

    private static final int loop = 3;

    @Benchmark
    public void awaitFinishAsync() throws InterruptedException {
        TracingContext context = new TracingContext();
        submit(TracingContext::awaitFinishAsync, context);
    }

    @Benchmark
    public void awaitFinishAsyncNoSynchronized() throws InterruptedException {
        TracingContext context = new TracingContext(new ReentrantLock());
        submit(TracingContext::awaitFinishAsyncNoSynchronized, context);
    }


    private void submit(Consumer<TracingContext> consumer, TracingContext context) throws InterruptedException {
        ExecutorService pool = new ThreadPoolExecutor(loop, loop, 1, TimeUnit.SECONDS, new LinkedBlockingDeque<>());
        CountDownLatch latch = new CountDownLatch(loop);
        for(int i = 0; i < loop; i++) {
            pool.execute(() -> {
                consumer.accept(context);
                latch.countDown();
            });
        }
        latch.await();
        pool.shutdownNow();
    }

    @BenchmarkMode(Mode.AverageTime)
    @OutputTimeUnit(TimeUnit.SECONDS)
    public static void main(String[] args) throws RunnerException {
        Options options = new OptionsBuilder().include(SynchronizedTest.class.getSimpleName()).build();
        new Runner(options).run();
    }
}
