@shirisht Testing out the replacement code in DummyCodeAgent, the new code appears to be faster than the older Guava-based code.

Here is a basic comparison:

``` java
package org.apache.sysml;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.Comparator;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Map.Entry;

import org.apache.commons.lang3.time.DurationFormatUtils;

import com.google.common.base.Functions;
import com.google.common.collect.Ordering;

public class GuavaPerfCheck {

    public static void main(String[] args) {

        Map<String, Long> mapStringLong = new HashMap<String, Long>();
        mapStringLong.put("One", 1L);
        mapStringLong.put("Eight", 8L);
        mapStringLong.put("Two", 2L);
        mapStringLong.put("Seven", 7L);
        mapStringLong.put("Three", 3L);
        mapStringLong.put("Six", 6L);
        mapStringLong.put("Four", 4L);
        mapStringLong.put("Five", 5L);
        List<String> orderedKeysGuava = getOrderedKeysGuava(mapStringLong);
        System.out.println("Ordered Keys Guava:" + Arrays.toString(orderedKeysGuava.toArray()));
        List<String> orderedKeysNoGuava = getOrderedKeysNoGuava(mapStringLong);
        System.out.println("Ordered Keys No Guava:" + Arrays.toString(orderedKeysNoGuava.toArray()));

        Map<String, String> mapStringString = new HashMap<String, String>();
        mapStringString.put("One", "1");
        mapStringString.put("Eight", "8");
        mapStringString.put("Two", "2");
        mapStringString.put("Seven", "7");
        mapStringString.put("Three", "3");
        mapStringString.put("Six", "6");
        mapStringString.put("Four", "4");
        mapStringString.put("Five", "5");
        List<String> orderedKeysGuava2 = getOrderedKeysGuava2(mapStringString);
        System.out.println("Ordered Keys Guava 2:" + Arrays.toString(orderedKeysGuava2.toArray()));
        List<String> orderedKeysNoGuava2 = getOrderedKeysNoGuava2(mapStringString);
        System.out.println("Ordered Keys No Guava 2:" + Arrays.toString(orderedKeysNoGuava2.toArray()));

        long start = System.currentTimeMillis();
        for (int i=0; i<10000000; i++) {
            getOrderedKeysGuava(mapStringLong);
        }
        System.out.println("Guava Duration:" + DurationFormatUtils.formatDurationHMS(System.currentTimeMillis()-start));

        start = System.currentTimeMillis();
        for (int i=0; i<10000000; i++) {
            getOrderedKeysNoGuava(mapStringLong);
        }
        System.out.println("No Guava Duration:" + DurationFormatUtils.formatDurationHMS(System.currentTimeMillis()-start));

        start = System.currentTimeMillis();
        for (int i=0; i<10000000; i++) {
            getOrderedKeysGuava2(mapStringString);
        }
        System.out.println("Guava 2 Duration:" + DurationFormatUtils.formatDurationHMS(System.currentTimeMillis()-start));

        start = System.currentTimeMillis();
        for (int i=0; i<10000000; i++) {
            getOrderedKeysNoGuava2(mapStringString);
        }
        System.out.println("No Guava 2 Duration:" + DurationFormatUtils.formatDurationHMS(System.currentTimeMillis()-start));
    }

    public static List<String> getOrderedKeysGuava(Map<String, Long> map) {
        Ordering<String> valueComparator = Ordering.natural().onResultOf(Functions.forMap(map));
        List<String> keysOrderedByValue = valueComparator.sortedCopy(map.keySet());
        return keysOrderedByValue;
    }

    public static List<String> getOrderedKeysNoGuava(Map<String, Long> map) {
        List<Map.Entry<String, Long>> entryList = new ArrayList<Map.Entry<String, Long>>(map.entrySet());
        Comparator<Map.Entry<String, Long>> comp = new Comparator<Map.Entry<String, Long>>() {
            @Override
            public int compare(Entry<String, Long> entry1, Entry<String, Long> entry2) {
                Long value1 = entry1.getValue();
                Long value2 = entry2.getValue();
                return (int) (value1 - value2);
            }
        };
        Collections.sort(entryList, comp);
        List<String> keysOrderedByValue = new ArrayList<String>();
        for (Entry<String, Long> entry : entryList) {
            keysOrderedByValue.add(entry.getKey());
        }
        return keysOrderedByValue;
    }

    public static List<String> getOrderedKeysGuava2(Map<String, String> map) {
        Ordering<String> orderByID = new Ordering<String>() {
            public int compare(String s1, String s2) {
                return (Integer.parseInt(s1) - Integer.parseInt(s2));
            }
        };

        List<String> keysOrderedByValue = orderByID.onResultOf(Functions.forMap(map)).sortedCopy(map.keySet());
        return keysOrderedByValue;
    }

    public static List<String> getOrderedKeysNoGuava2(Map<String, String> map) {
        List<Map.Entry<String, String>> entryList = new ArrayList<Map.Entry<String, String>>(map.entrySet());
        Comparator<Map.Entry<String, String>> comp = new Comparator<Map.Entry<String, String>>() {
            @Override
            public int compare(Entry<String, String> entry1, Entry<String, String> entry2) {
                String value1 = entry1.getValue();
                String value2 = entry2.getValue();
                return (Integer.parseInt(value1) - Integer.parseInt(value2));
            }
        };
        Collections.sort(entryList, comp);
        List<String> keysOrderedByValue = new ArrayList<String>();
        for (Entry<String, String> entry : entryList) {
            keysOrderedByValue.add(entry.getKey());
        }
        return keysOrderedByValue;
    }

}
