Example Java class that utilizes the single SystemML dependency:
```
package org.apache.systemml.example;

import java.util.ArrayList;
import java.util.List;

import org.apache.spark.SparkConf;
import org.apache.spark.api.java.JavaRDD;
import org.apache.spark.api.java.JavaSparkContext;
import org.apache.sysml.api.mlcontext.MLContext;
import org.apache.sysml.api.mlcontext.Script;
import org.apache.sysml.api.mlcontext.ScriptFactory;

public class MLContextDemoExample {
	public static void main(String[] args) throws Exception {
		SparkConf conf = new SparkConf().setAppName("MLContextDemoExample").setMaster("local");
		JavaSparkContext sc = new JavaSparkContext(conf);
		MLContext ml = new MLContext(sc);

		Script uni = ScriptFactory.dmlFromResource("/scripts/algorithms/Univar-Stats.dml");
		String habermanUrl = "http://archive.ics.uci.edu/ml/machine-learning-databases/haberman/haberman.data";
		uni.in("A", new java.net.URL(habermanUrl));
		List<String> list = new ArrayList<String>();
		list.add("1.0,1.0,1.0,2.0");
		JavaRDD<String> typesRDD = sc.parallelize(list);
		uni.in("K", typesRDD);
		uni.in("$CONSOLE_OUTPUT", true);
		ml.execute(uni);	
	}
}
