    @Override
    public ArchiveInputStream createArchiveInputStream(String archiverName, InputStream in, String actualEncoding) throws ArchiveException {
        if (archiverName == null) {
            throw new IllegalArgumentException("Archivername must not be null.");
        }

        if (in == null) {
            throw new IllegalArgumentException("InputStream must not be null.");
        }

        if (ZIP.equalsIgnoreCase(archiverName)) {
            Optional<String> maybeEncoding = Optional.of(actualEncoding);
            return new ZipArchiveInputStream(in, maybeEncoding.orElse(ZipEncodingHelper.UTF8), useUnicodeExtraFields, allowStoredEntriesWithDataDescriptor, skipSplitSig);
        } else {
            return super.createArchiveInputStream(archiverName, in, actualEncoding);
        }
    }
