	public static void addEventListener(final IHeaderResponse headerResponse, Component component, String event, String eventParamName, CharSequence javascript, boolean priority)
	{
		OnDomReadyHeaderItem item = OnDomReadyHeaderItem.forScript(
				String.format(
						"var el = document.getElementById('%s'); if(el){el.addEventListener('%s', function(%s){ %s; });}",
						component.getMarkupId(),
						event,
						eventParamName,
						javascript
				)
		);
		headerResponse.render(priority ? new PriorityHeaderItem(item) : item);
	}
```

