>                 var sender = new HttpZipkinSender("http://localhost:9411", "application/json");
>                 var serializer = new JSONSpanSerializer();
>                 var tracer = new ZipkinTracer(sender, serializer);
>                 TraceManager.RegisterTracer(tracer);
>                 TraceManager.Start(logger);
>
>                 // Timings
>                 var uiStart = UtcNow;
>                 var gatewayStart = uiStart.AddMilliseconds(10);
>                 var gatewayCallAuthStart = gatewayStart.AddMilliseconds(10);
>                 var gatewayCallProfileStart = gatewayStart.AddMilliseconds(20);
