>                 var uiEnd = gatewayEnd.AddMilliseconds(10);
>
>                 // UI Call Gateway
>                 var uiTrace = Trace.Create();
>                 uiTrace.Record(Annotations.ServiceName("client"));
>                 uiTrace.Record(Annotations.Rpc("get-user"));
>                 uiTrace.Record(Annotations.ClientSend(), uiStart);
>                 uiTrace.Record(Annotations.ClientRecv(), uiEnd);
>
>                 // Gateway
>                 var gatewayTrace = uiTrace.Child();
>                 gatewayTrace.Record(Annotations.ServiceName("api-gateway"));
>                 gatewayTrace.Record(Annotations.Rpc("get-user"));
>                 gatewayTrace.Record(Annotations.ServerRecv(), gatewayStart);
>                 gatewayTrace.Record(Annotations.ServerSend(), gatewayEnd);
>
>                 // Gateway Call Auth
>                 var gatewayCallAuthTrace = gatewayTrace.Child();
>                 gatewayCallAuthTrace.Record(Annotations.ServiceName("auth-service"));
>                 gatewayCallAuthTrace.Record(Annotations.Rpc("get-permissions"));
>                 gatewayCallAuthTrace.Record(Annotations.ClientSend(), gatewayCallAuthStart);
>                 gatewayCallAuthTrace.Record(Annotations.ClientRecv(), gatewayCallAuthEnd);
>
>                 // Gateway Call Profile
>                 var gatewayCallProfileTrace = gatewayTrace.Child();
>                 gatewayCallProfileTrace.Record(Annotations.ServiceName("api-gateway"));
>                 gatewayCallProfileTrace.Record(Annotations.Rpc("get-profile"));
>                 gatewayCallProfileTrace.Record(Annotations.ClientSend(), gatewayCallProfileStart);
>                 gatewayCallProfileTrace.Record(Annotations.ClientRecv(), gatewayCallProfileEnd);
>
>                 // Auth Service
>                 var authTrace = gatewayCallAuthTrace.Child();
>                 authTrace.Record(Annotations.ServiceName("auth-service"));
>                 authTrace.Record(Annotations.Rpc("get-permissions"));
>                 authTrace.Record(Annotations.ServerRecv(), authStart);
>                 authTrace.Record(Annotations.ServerSend(), authEnd);
>
>                 // Profile Service
>                 var profileTrace = gatewayCallProfileTrace.Child();
>                 profileTrace.Record(Annotations.ServiceName("profile-service"));
>                 profileTrace.Record(Annotations.Rpc("get-profile"));
>                 profileTrace.Record(Annotations.ServerRecv(), profileStart);
>                 profileTrace.Record(Annotations.ServerSend(), profileEnd);
>
>
>                 // Wait for key to close program
>                 Console.ReadKey(true);
>                 TraceManager.Stop();
>                 TraceManager.ClearTracers();
>             }
>         }
>
>         static DateTime UtcNow => _startDateTime.AddTicks(System.Diagnostics.Stopwatch.GetTimestamp() - _startTicks);
>     }
