                compbuf.clear();
                ++ncomponent;
            }
        } else if (*c == '"' || *c == '\'' && (is_begin || is_end)) {  // Check quotation.
            if (!quote_char) {  // begin quote
                quote_char = *c;
                quote_pos = c;
            } else if (quote_char == *c) {  // end quote
                is_empty_component = (c - quote_pos == 1) ? true : false; // for empty string
                quote_char = 0;
            } else {
                compbuf.push_back(*c);
            }
        } else {
            compbuf.push_back(*c);
        }
        is_begin = false;
        if (c == cmd.data() + cmd.size() - 2) {
         is_end = true;
         }
    }
