void* Socket::HealthCheckThread(void* void_arg) {
    SocketId socket_id = (SocketId)void_arg;
    bool first_time = true;
    if (bthread_usleep(100000) < 0) {
        PLOG_IF(FATAL, errno != ESTOP) << "Fail to sleep";
        return NULL;
    }
}
