message EndPoint {
    // process name
    string serviceName = 1;
    string ipv4 = 2;
}

message Annotation {
    // unit: microsecond
    int64 timestamp = 1;
    // Should be one of: cs, cr, ss, sr, which stands for
    // client send, client receive, server send, server receive respectively.
    string value = 2;
    EndPoint endpoint = 3;
}

// Used to transfor custom data.
message BinaryAnnotation {
    string key = 1;
    string value = 2;
    EndPoint endpoint = 3;
}

message Span {
    string traceId = 1;
    // span id
    string id = 2;
    string parentId = 3;
    // method name
    string name = 4;
    // unit: microsecond
    int64 timestamp = 5;
    // unit: microsecond
    uint32 duration = 6;
    repeated Annotation annotations = 7;
    repeated BinaryAnnotation binaryAnnotations = 8;
}
