		System.setProperty("java.security.krb5.conf", getString(KERBEROS_CONF_KRB5));
		conf.set("kerberos.principal", getString(KERBEROS_PRINCIPAL));
		conf.set("hadoop.security.authorization", "true");
		conf.set("hadoop.security.authentication", "kerberos");
