  {
    return new PartitionedDataRecordReader(
        delegate.createRecordReader(split, context),
        HadoopDruidIndexerConfig.fromConfiguration(context.getConfiguration()).getParser()
    );
  }
}

public class PartitionedDataRecordReader extends RecordReader<NullWritable, InputRow>
{

  private RecordReader delegate;
  private InputRowParser parser;

  public PartitionedDataRecordReader(RecordReader delegate, InputRowParser parser)
  {
    this.delegate = delegate;
    this.parser = parser;
  }

  @Override
  public boolean nextKeyValue() throws IOException, InterruptedException
  {
    return delegate.nextKeyValue();
  }

  @Override
  public InputRow getCurrentValue() throws IOException, InterruptedException
  {
    final InputRow original = parser.parse(delegate.getCurrentValue());
    //wrap original row with another that has additional dimensions
    return new InputRow() {
      //wraps original
    };
  }
}

