```java
if (bestServers.stream().allMatch(server -> server.lhs != Double.POSITIVE_INFINITY)) {
   // Randomly choose a server from the best servers
   bestServer = bestServers.get(ThreadLocalRandom.current().nextInt(bestServers.size()));
}
