```
function randomData() {
    return Math.random() * 1000
}

function getRandomColor(){
  return '#'+('00000'+(Math.random()*0x1000000<<0).toString(16)).slice(-6);
}

var option = {
    graphic: {
      id:'text1',
      type: 'rect',
      shape: {
          x:0,
          y:0,
          width: 100,
          height: 100,
      },
      style: {
          fill: 'red',
      }
    },
    xAxis: {
        data: ['1', '2', '3']
    },
    yAxis: { },
    series: [{
        name: '',
        type: 'bar',
        data: [randomData(),randomData(),randomData()]
    }]
};

var myChart = echarts.init(document.getElementById('main'));
myChart.setOption(option);

setInterval(function () {
    var option = myChart.getOption();
    option.series[0].data = [randomData(), randomData(), randomData()];
    // option.graphic = {
    //   type: 'rect',
    //   shape: {
    //       x:0,
    //       y:0,
    //       width: 100,
    //       height: 100,
    //   },
    //   style: {
    //       fill: getRandomColor(),
    //   }
    // },
    option.graphic = { // 删除上例中定义的 'text1' 元素。
            id: 'text1',
            $action: 'remove'
        };
    myChart.setOption(option);
}, 1000);
```
