function convertDataAll(data, type) {
	return convertData(data, type, 'All');
};

function convertDataA1(data, type) {
	return convertData(data, type, 'A1');
};

function convertDataA2(data, type) {
	return convertData(data, type, 'A2');
};

function convertData(data, type, geoCoordMap) {
	var res = [];
	for(var i = 0; i < data.length; i++) {
		eval('var geoCoord = geoCoordMap' + geoCoordMap + '[data[i].name];');
		if(geoCoord) {
				for(var j = 0; j <= Math.floor(661 / data.length) - 1; j++) {
					if(type == "true") {
						if(data[i].value > 5) {
							res.push({
								name: data[i].name,
								value: geoCoord.concat(data[i].value)
							});
						}
					} else {
						if(data[i].value <= 5) {
							res.push({
								name: data[i].name,
								value: geoCoord.concat(data[i].value)
							});
						}
					}
				}
		}
	}
	return res;
}

