    tooltip: {
      trigger: 'axis',
      formatter: function(params) {

        if (params.length > 2) {

          var kline;
          var increase;
          var amplitude;
          var ma5;
          var ma10;
          var item;

          for (var i = 0; i < params.length; i++) {

            item = params[i];

            switch (item.seriesIndex) {

              case 0:

                kline = item.name + ' &nbsp; 开：' + item.value[1].toFixed(2) + ' &nbsp; 高：' + item.value[4].toFixed(2) + ' &nbsp; 低：' + item.value[3].toFixed(2) + ' &nbsp; 收：' + item.value[2].toFixed(2);

                increase = '涨幅：' + ((item.value[2] - item.value[1]) / item.value[1] * 100).toFixed(2) + '%';

                amplitude = '振幅：' + ((item.value[4] - item.value[3]) / item.value[1] * 100).toFixed(2) + '%';

                break;

              case 1:

                ma5 = 'MA5: ' + item.value.toFixed(2);

                break;

              case 2:

                ma10 = 'MA10: ' + item.value.toFixed(2);

                break;

            }

          }

          //return kline + ' &nbsp; ' + increase + ' &nbsp; ' + amplitude + ' &nbsp; <span style="color: #ffffff">' + ma5 + '</span> &nbsp; <span style="color: #ffff33">' + ma10 + '</span>';

          return kline + ' &nbsp; ' + increase + ' &nbsp; ' + amplitude;

        }

      },
      textStyle: {
        color: '#bababa',
        fontSize: 12
      },
      backgroundColor: 'transparent',
      padding: [0, 6],
      position: [3, 3],
      axisPointer: {
        type: 'cross',
        crossStyle: {
          type: 'solid'
        }
      }
    },
    axisPointer: {
      link: {
        xAxisIndex: [0]
      },
      label: {
        color: '#bababa',
        backgroundColor: '#000000',
        borderColor: '#bababa',
        borderWidth: 1,
        padding: [3, 6]
      },
      zlevel: 2
    },
    dataZoom: [{
      type: 'inside',
      start: _this.zoomStart,
      minSpan: 3,
      xAxisIndex: [0, 1, 2]
    }, {
      show: false
    }],
    series: [
      {
        name: 'K线',
        type: 'candlestick',
        dimensions: ['时间', '开', '收', '低', '高'],
        data: this.data.ohlcs,
        animation: false,
        markPoint: {
          symbolSize: [1, 1],
          itemStyle: {
            normal: {
              color: 'transparent'
            }
          },
          data: [{
            type: 'max',
            name: '最大值',
            valueDim: '高',
            symbolOffset: [0, -9],
            label: {
              normal: {
                color: '#bababa'
              }
            }
          }, {
            type: 'min',
            name: '最小值',
            valueDim: '低',
            symbolOffset: [0, 9],
            label: {
              normal: {
                color: '#bababa'
              }
            }
          }]
        },
        itemStyle: {
          normal: {
            color: 'transparent',
            color0: '#75d75c',
            borderColor: '#dd5d36',
            borderColor0: '#75d75c'
          }
        },
        zlevel: 1
      }, {
        name: 'MA5',
        type: 'line',
        data: this.data.ma5,
        smooth: true,
        showSymbol: false,
        animation: false,
        zlevel: 1,
        lineStyle: {
          normal: {
            width: 1,
            color: '#ffffff'
          }
        }
      }, {
        name: 'MA10',
        type: 'line',
        data: this.data.ma10,
        smooth: true,
        showSymbol: false,
        animation: false,
        zlevel: 2,
        lineStyle: {
          normal: {
            width: 1,
            color: '#ffff33'
          }
        }
      }, {
        name: 'Asks',
        type: 'line',
        xAxisIndex: 1,
        yAxisIndex: 1,
        data: this.data.yAsk,
        showSymbol: false,
        animation: false,
        lineStyle: {
          normal: {
            width: 1,
            color: '#dd5d36'
          }
        },
        areaStyle:{
          normal:{
            color: {
              type: 'linear',
              x: 1,
              y: 0,
              x2: 0,
              y2: 0,
              colorStops: [{
                offset: 0, color: '#dd5d36' // 0% 处的颜色
              }, {
                offset: 1, color: '#000000' // 100% 处的颜色
              }],
              globalCoord: false // 缺省为 false
            }
          }
        }
      }, {
        name: 'Bids',
        type: 'line',
        xAxisIndex: 2,
        yAxisIndex: 2,
        data: this.data.yBid,
        showSymbol: false,
        animation: false,
        lineStyle: {
          normal: {
            width: 1,
            color: '#75d75c'
          }
        },
