        min: -250,
        max: -150
    },
    series: [{
        data: [100, 200, 300],
        type: 'line'
    }]
};

```

#### 解决了，方法是单独定义controller和target的inRange中color属性。
#### 修改时也是同时修改controller和target的inRange中color属性。
```javascript
//之前的定义
visualMap.push({
	id: keyName,
	show: false,
	realtime:false,
    seriesIndex:this.number,
    min: parseFloat(min),
    max: parseFloat(max),
    left: 'right',
    bottom: '45%',
    calculable: true,
    precision:1,
    inRange: {
         color: [options.color[this.number]]
    },
})

//修改后定义
visualMap.push({
    id: keyName,
    show: false,
    realtime:false,
    seriesIndex:this.number,
    min: parseFloat(min),
    max: parseFloat(max),
    left: 'right',
    bottom: '45%',
    calculable: true,
    precision:1,
    controller:{
    	inRange: {
            color: [options.color[this.number]]
        }
    },
    target:{
    	inRange: {
            color: [options.color[this.number]]
        }
    },
})

//修改时
let newVisualMap = options.visualMap.map((item, index) =>{
	if(item.name === keyName){
		return item
	}else{
		if(index > posInArr){
			item.seriesIndex = item.seriesIndex -1
			item.controller.inRange.color = [options.color[index - 1]]
			item.target.inRange.color = [options.color[index - 1]]
		}
		return item
	}
})
```

