function dataFormatter(obj) {
    var temp;
    var max = 0;
    for (var year = dataInfo.symbols[0]; year <= dataInfo.symbols[dataInfo.symbols.length]; year++) {
        temp = obj[year];
        for (var i = 0, l = temp.length; i < l; i++) {
            max = Math.max(max, temp[i]);
