            "parentid": "1_173",
            "category": "None",
            "symbolSize": 50,
            symbol: 'circle',
            "itemStyle": {
                "borderColor": "#000",
                "borderWidth": 5,
                color: 'transparent'
            }
        }
...
links: [{
...
