            "source": 17,
            "target": 18,
            lineStyle: {
                color: 'green',
                opacity: 1
            }
        }
...
```

```js
option = {
    title: {
        text: 'Awesome Chart'
    },
    xAxis: {
        data: ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'],
        axisPointer: {
            show: true,
            snap: true
        }
    },
    yAxis: {
        axisPointer: {
            show: true,
            snap: true
        }
    },
    series: [{
        type: 'line',
        data:[220, 182, 191, 234, 290, 330, 310]
    }]
};
```

