                                                '</span>' + "<span style='font-size: 14px;color: #FF9100;'>" +
                                                '   ' + '</span>' +
                                                "<span style='font-size: 14px;color: #FF9100;'>" +
                                                params[i].value + '%</span>';
                                        }

                                    }
                                    return res;
                                },
                                triggerOn: 'mousemove',
                                //坐标轴指示器
                                axisPointer: {
                                    type: 'line',
                                    snap: true,
