                                {
                                    type: 'category',
                                    boundaryGap: false, //默认为true留空，false则顶头
                                    scale: true,
                                    splitNumber: 10, //最大最小间隔
                                    axisLine: {
                                        show: true,
                                        onZero: true,
                                        lineStyle: {
                                            color: 'rgba(255,255,255,0.2)',
                                            width: 1,
                                            type: 'solid'
                                        }
                                    },
                                    //x轴的小标记，让小标记展示为false
                                    axisTick: {
                                        show: false,
                                    },
                                    //x轴文本 axisLabel
                                    axisLabel: {
                                        margin: 6,
                                        interval: number,//1093,//为0则全部展示 为1 表示相隔一个展示，一次类推
                                        // rotate:90, 文字旋转角度
                                        textStyle: {
                                            color: 'rgba(255,255,255,0.2)',
                                            fontSize: 10,
                                        },
                                    },
                                    //分割线
                                    splitLine: {
                                        show: true,
                                        onGap: false,
                                        lineStyle: {
                                            color: 'rgba(255,255,255,0.1)',
                                            width: 1,
                                            type: 'dashed'
                                        }
                                    },
                                    data: rowArray

                                }
                            ],
                            //{Array | Object} yAxis y轴
