                                        onZero: false,
                                        lineStyle: {
                                            color: 'rgba(255,255,255,0.1)',
                                            width: 0.5,
                                            type: 'dashed'
                                        }
                                    },
                                    axisTick: {
                                        show: false,
                                    },
                                    axisLabel: {
                                        formatter: '{value} %',
                                        textStyle: {
                                            color: '#7d9ff7'
                                        }
                                    },
                                    axisPointer: {
                                        show: false,
                                    },

                                    splitLine: {
                                        show: true,
                                        onGap: false,
                                        lineStyle: {
                                            color: 'rgba(255,255,255,0.1)',
                                            width: 1,
                                            type: 'solid'
                                        }
                                    }

                                }
                            ],
                            // 数据{Array} series
                            series: [
                                {
                                    name: _this.typeText2 + '配置',
                                    type: 'line',
                                    // symbol: 'none',
                                    symbol: 'image:///app/account/asset/customize/icons/blue-icon.png',
                                    showSymbol: false,
                                    symbolSize: 4,
                                    symbolKeepAspect: true,
                                    data: ptArray,
                                    smooth:true,
                                    itemStyle: {
                                        normal: {

                                            lineStyle: {
                                                color: '#fff',
                                                type: "solid",
                                                width: 2,

                                            },
                                            areaStyle: {

                                                //颜色渐变函数 前四个参数分别表示四个位置依次为左、下、右、上
                                                color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                                                    offset: 0, color: '#2c5ef0' // 0% 处的颜色
                                                }, {
                                                    offset: 0.1, color: '#3d6bf1' // 40% 处的颜色
                                                }, {
                                                    offset: 1, color: '#4671ec' // 100% 处的颜色
                                                }]
                                                ), //背景渐变色   


                                            },
                                        },
                                    }
                                },
                                {
                                    name: title,
                                    type: 'line',
                                    // symbol: 'none',
                                    symbol: 'image:///app/account/asset/customize/icons/or-icon.png',
                                    showSymbol: false,
                                    symbolSize: 4,
                                    symbolKeepAspect: true,
                                    data: array,
                                    smooth:true,
                                    itemStyle: {
                                        normal: {
                                            lineStyle: {
                                                color: 'rgba(255,255,255,0.5)',
                                                type: "solid",
                                                width: 1,
                                            },
                                            // emphasis: {
                                            //     borderColor: 'blue'
                                            // }
                                        }
                                    },
                                    // markPoint:{
                                    //     clickable:true,
                                    //     symbol:'symbolSize',
                                    //     symbolSize:'20',
                                    //     itemStyle:{
                                    //         emphasis:{
                                    //             borderColor:'blue'
                                    //         }
                                    //     }
                                    // }
                                }
                            ],
                            // 直角坐标系，内的网格图{Object} grid
                            grid: {
                                backgroundColor: 'rgba(0,0,0,1)',
                                borderWidth: 3,
                                x: '13%',
