这是林峰大拿回答的示例，交给前端照猫画虎吧。

var data = [
  {value:100, name:'直接访问'},
  {value:200, name:'邮件营销'}
]
option = {
    legend: {
        orient : 'vertical',
        x : 'left',
        formatter: function(name){
          var total = 0;
          var target;
          for (var i = 0, l = data.length; i < l; i++) {
            total += data[i].value;
            if (data[i].name == name) {
              target = data[i].value;
            }
          }
          return name + ' ' + ((target/total)*100).toFixed(2) + '%';
        },
        data:['直接访问','邮件营销']
    },
    series : [
        {
            name:'访问来源',
            type:'pie',
            data: data
        }
    ]
};

