/**
 * 当前瀑布图按照 stack bar 实现
 * 把原始数值按正负拆分为upData和downData
 * 有一个series为辅助bar，通过设置颜色为透明达到类似瀑布图效果
 * 辅助bar的值为当前index之前几个数的求和，
 * 
 * 注意：由于是堆积柱形图，所以瀑布图无法实现从一个正值直接下降到负值的情况
 *      如：从400下降800达到-400这样的效果做不到
 */ 

const originData = [900, 345, 393, -108, -154, 135, 178, 286, -119, -361, -203]

const upData = []
const downData = []
const braceData = []
const PLACEHOLDER = '-';

for (let i = 0; i < originData.length; i++) {
  const val = originData[i];
  // 正增负减
  if (val > 0) {
    upData.push(val);
    downData.push(PLACEHOLDER);
  } else {
    downData.push(-val);
    upData.push(PLACEHOLDER);
  }

  if (i === 0) {
    braceData[i] = 0;
  } else {
    // 直接根据 originData 计算
    braceData[i] = braceData[i - 1] + originData[i - 1];

    // 基于 upData 和 downData 计算辅助数列
    // braceData[i] = braceData[i - 1], (upData[i - 1] === PLACEHOLDER ? 0 : upData[i - 1]) - (downData[i] === PLACEHOLDER ? 0 : downData[i])
  }
}

// output
originData = [900, 345, 393, -108, -154, 135, 178, 286, -119, -361, -203]
upData = [900, 345, 393, '-', '-', 135, 178, 286, '-', '-', '-']
downData = ['-', '-', '-', 108, 154, '-', '-', '-', 119, 361, 203]
braceData = [0, 900, 1245, 1530, 1376, 1376, 1511, 1689, 1856, 1495, 1292]

```

