let yData = [2, 4, 5, 6, 8, 9, 10, 14, 17, 23, 30];
let startvalue = 1;
let interval = 2;
let data = yData.map((v, i) => [startvalue + i * interval, v])

option = {
    xAxis: {
    },
    yAxis: {
        type: 'value'
    },
    series: [{
        data: data,
