var option = {
                animation: false,
                title: {
                    text: this.title + '(' + this.hqDate + ')'
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer:{
                        type : 'cross',
                        crossStyle: {
                            type : 'solid',
                            width : 1,
                            color: '#000'
                        }
                    },
                    showDelay: 0, // 显示延迟，添加显示延迟可以避免频繁切换，单位ms
                    hideDelay: 0,
                    transitionDuration: 0,
                    position: [22, 40],
                    backgroundColor: 'rgba(255,255,255,1)',
                    borderRadius: 0,
                    borderWidth: 1,
                    textStyle: {
                        color: '#000'
                    },
                    formatter: function(params) {
                        var lastClosePrice;
                        var res = '<div">';

                        array.each(params, function(param, i){
                            if(!param.seriesIndex) {
                                //res += '<div>' + param.name + '</div>';
                                res += string.format(me.klineDetailTitleTpl, {
                                    name: param.name.substring(5, 10),
                                    value: param.name.substring(11, 16)
                                });

                                if (param.dataIndex === 0) {
                                    lastClosePrice = param.value[1];
                                } else {
                                    lastClosePrice = param.series.data[param.dataIndex - 1][1];
                                }

                                var openPrice = param.value[0],
                                    maxPrice = param.value[3],
                                    minPrice = param.value[2],
                                    closePrice = param.value[1];

                                res += string.format(me.klineDetailTpl, {
                                    name: '开盘',
                                    value: openPrice,
                                    color: me.colorMap[openPrice >= lastClosePrice ? 'gain' : 'loss']
                                });
                                res += string.format(me.klineDetailTpl, {
                                    name: '最高',
                                    value: maxPrice,
                                    color: me.colorMap[maxPrice >= lastClosePrice ? 'gain' : 'loss']
                                });
                                res += string.format(me.klineDetailTpl, {
                                    name: '最低',
                                    value: minPrice,
                                    color: me.colorMap[minPrice >= lastClosePrice ? 'gain' : 'loss']
                                });
                                res += string.format(me.klineDetailTpl, {
                                    name: '收盘',
                                    value: closePrice,
                                    color: me.colorMap[closePrice >= lastClosePrice ? 'gain' : 'loss']
                                });
                            } else {
                                if (param.seriesName !== '交易记录') {
                                    res += string.format(me.klineDetailTpl, {
                                        name: param.seriesName,
                                        value: param.value,
                                        color: me.colorMap[param.seriesName]
                                    });
                                }
                            }
                        });

                        res += '</div>';
                        return res;
                    }
                }
